package com.smart.light.exporter.itextpdfimport com.itextpdf.text.Documentimport com.itextpdf.text.PageSizeimport com.itextpdf.text.pdf.BaseFontimport com.itextpdf.text.pdf.PdfWriter/** * * lvpeng on 2015/8/11. */class PdfExporter {    public Document document = new Document(PageSize.A4, 0f, 0f, 0f, 0f)    /**     *     */    public void setPdfAttributes(Closure titleClosure = this.&defaultAttribute) {        titleClosure.call()    }    /**     *     */    private void defaultAttribute() {        document.addTitle('')        document.addSubject('')        document.addAuthor('')        document.addCreationDate()    }    /**     *     */    public void doExport(OutputStream outputStream) {        PdfWriter.getInstance(document, outputStream)//        document.close()    }    /**     *     */    public buildTable(BaseFont baseFont, Closure table) {        table.call(document)    }}