package com.smart.light.exporter.itextpdfimport com.itextpdf.text.Fontimport com.itextpdf.text.Paragraphimport com.itextpdf.text.pdf.BaseFontimport com.itextpdf.text.pdf.PdfPTable/** * * lvpeng on 2015/8/11. */class PdfUtils {    public PdfUtils(String chineseFile) {        this.chineseFile = chineseFile    }    /**     *     */    public String chineseFile = null    public BaseFont baseFont() {        return BaseFont.createFont(chineseFile + ',1', BaseFont.IDENTITY_H, BaseFont.EMBEDDED)    }    public Font defaultFont() {        return new Font(baseFont())    }    public PdfPTable defaultTable(String [] title,Closure closure){        PdfPTable table = new PdfPTable(title.length)        title.each {            table.addCell(new Paragraph(it, defaultFont()))        }        closure.call(table)        return table    }    public PdfPTable explainTable(String[] list){        PdfPTable table = new PdfPTable(4)        def width = [10,20,10,20] as int[]        table.setWidths(width)        list.eachWithIndex { String entry, int i ->            def cell = new Paragraph(entry, defaultFont())            table.addCell(cell)        }        return table    }}