package com.smart.light.exporter.poi.cellStyleimport org.apache.poi.hssf.util.HSSFColorimport org.apache.poi.ss.usermodel.CellStyleimport org.apache.poi.ss.usermodel.Fontimport org.apache.poi.ss.usermodel.Workbook/** * 单元格样式定义集合 * lvpeng */class CellStyleSet {    final Workbook workbook    CellStyleSet(Workbook workbook) {        this.workbook = workbook    }    /**     * 海绿 灰框 标题     * @return     */    CellStyle seaGreenTitleStyle() {        Font font = workbook.createFont()        CellStyle cellStyle = workbook.createCellStyle()        font.setColor(HSSFColor.BLACK.index) //字体颜色        font.setBoldweight(Font.BOLDWEIGHT_BOLD) //字体粗细        font.setFontHeightInPoints((short) 12);        cellStyle.setWrapText(true)        cellStyle.setFont(font)        cellStyle.setVerticalAlignment((short) 1) //垂直居中        cellStyle.setAlignment(CellStyle.ALIGN_CENTER) //水平居中        this.setCellBorderAndColor(cellStyle,(short)1,HSSFColor.GREY_25_PERCENT.index)//        cellStyle.setFillPattern(CellStyle.SOLID_FOREGROUND) //主题//        cellStyle.setFillForegroundColor(HSSFColor.SKY_BLUE.index)        return cellStyle    }    /**     * 白底灰框     * @return     */    CellStyle whiteStyle() {        Font font = workbook.createFont()        CellStyle cellStyle = workbook.createCellStyle()        font.setFontHeightInPoints((short) 12);//字体大小        cellStyle.setWrapText(true)        cellStyle.setFont(font)        cellStyle.setVerticalAlignment((short) 1) //垂直居中        cellStyle.setAlignment(CellStyle.ALIGN_CENTER) //水平居中        this.setCellBorderAndColor(cellStyle,(short)1,HSSFColor.GREY_25_PERCENT.index)//        cellStyle.setFillPattern(CellStyle.SOLID_FOREGROUND) //主题        return cellStyle    }    /**     * 设置cell的边框和边框颜色     * @param cellStyle     * @param border 边框大小     */    private void setCellBorderAndColor(CellStyle cellStyle, short border, short color) {        cellStyle.setBorderBottom(border)        cellStyle.setBorderLeft(border)        cellStyle.setBorderRight(border)        cellStyle.setBorderTop(border)        cellStyle.setBottomBorderColor(color)        cellStyle.setTopBorderColor(color)        cellStyle.setLeftBorderColor(color)        cellStyle.setRightBorderColor(color)    }}